#!/usr/bin/env python3
"""
Create simple PNG icons for Chrome extension
"""

from PIL import Image, ImageDraw
import os

def create_png_icon(size, filename):
    """Create a simple PNG icon"""
    # Create image with transparent background
    img = Image.new('RGBA', (size, size), (0, 0, 0, 0))
    draw = ImageDraw.Draw(img)
    
    # Draw phone shape
    margin = size // 8
    phone_width = size - 2 * margin
    phone_height = size - 2 * margin
    
    # Phone body (rounded rectangle)
    draw.rounded_rectangle(
        [margin, margin, margin + phone_width, margin + phone_height],
        radius=size // 16,
        fill=(0, 123, 255, 255),  # Blue color
        outline=(0, 86, 179, 255),  # Darker blue
        width=2
    )
    
    # Screen
    screen_margin = margin + size // 16
    draw.rounded_rectangle(
        [screen_margin, screen_margin + size // 16, 
         size - screen_margin, size - screen_margin - size // 16],
        radius=size // 32,
        fill=(255, 255, 255, 255),
        outline=(200, 200, 200, 255),
        width=1
    )
    
    # Home button (for larger icons)
    if size >= 48:
        button_size = size // 16
        button_x = size // 2 - button_size // 2
        button_y = size - margin - button_size - 2
        draw.ellipse(
            [button_x, button_y, button_x + button_size, button_y + button_size],
            fill=(255, 255, 255, 255),
            outline=(0, 86, 179, 255),
            width=1
        )
    
    # Save the image
    img.save(filename, 'PNG')
    print(f"Created {filename}")

def main():
    # Create icons directory if it doesn't exist
    icons_dir = os.path.dirname(os.path.abspath(__file__))
    
    try:
        # Create PNG icons
        create_png_icon(16, os.path.join(icons_dir, 'icon16.png'))
        create_png_icon(48, os.path.join(icons_dir, 'icon48.png'))
        create_png_icon(128, os.path.join(icons_dir, 'icon128.png'))
        
        print("✅ PNG icons created successfully!")
        
    except ImportError:
        print("❌ PIL (Pillow) not available. Creating simple base64 icons...")
        create_simple_icons()

